import React, { Component } from 'react';
import { graphql } from 'react-apollo';

import { getBookQuery } from '../queries/Queries'

class BookDetail extends Component {

      constructor(props){
        super(props)
        this.displayBookDetail = this.displayBookDetail.bind(this)
      }

      displayAuthorBooks(e){
        <ul>
          {book.author.books.map(item=>{
            return<li key ={item.id}> {item.name} </li>
          })}
        </ul>
      }

      displayBookDetail(e){
      const {book} = this.props.data
      if(book)
      {
        return(
          <div >
            <h2>{book.name}</h2>
            <p>{book.genre}</p>
            <p>{book.author.name}</p>
            <p> other books by the author</p>
            {this.displayAuthorBooks()}
          </div>
        );
      }
      else{
        return(
          <div >
            No Book selected...
          </div>
        )
      }

  }

    render(){


        return(
              <div id="book-details">
                <ul>
                    <p> {this.displayBookDetail()}</p>
                </ul>
            </div>
        );
    }
}

export default graphql(getBookQuery,{
  options:(props)=>{
    return{
      variables:{
        id:props.bookid
      }
    }
  }
})(BookDetail);
