import React, { Component } from 'react';

import Navigation from "../layout/Navigation";

class Skeleton extends Component {

  constructor(props){
      super(props);
      this.state = {
          show:false,
          value:"",
          user_name: "",
          books:[],
      }
  }

  addItem = ()=>{
    this.setState(state=>{
      const books = state.books.concat (state.value);
      return{
        books
      }
    })
  }

  render() {
    return (
        <div>
        <Navigation/>


        <div style={{position:"relative",marginTop:"20px",marginLeft:"20px"}}>
        <input type="text"  onChange={(e)=>this.setState({user_name:e.target.value})}/> Enter your name <br/>
        <input type="text"  onChange={(e)=>this.setState({value:e.target.value})}/> enter the name of the book/books that've infuenced you
        <button onClick={this.addItem} >Add Book</button>
        {console.log(this.state.test)}

        </div>

        <div id="book-details" style={{top:"56px"}}>
          <h1>Name:{this.state.user_name}</h1>
          <h2>Books:
            <ul>
            {this.state.books.map(item=><li>item</li>)}
            </ul>
           </h2>
        </div>
        </div>

    );
  }
}
export default Skeleton;
