import { graphql } from 'react-apollo';
import { getBooksQuery } from '../queries/queries';

// components
// import BookDetails from './BookDetails';

class BookList extends Component {
    constructor(props){
        super(props);
        this.state = {
            selected: null
        }
    }
    displayBooks(){
        var data = this.props;
        return( <div>Loading books check console...</div> );
