        // } else {
        //     return data.books.map(book => {
        //         return(
        //             <li key={ book.id } onClick={ (e) => this.setState({ selected: book.id }) }>{ book.name }</li>
        //         );
        //     })
        // }
    }
    render(){
        return(
            <div>
                <ul id="book-list" className="site-font">
                    { this.displayBooks() }
                </ul>

            </div>
        );
    }
}

export default graphql(getBooksQuery,{
  options: (props)=>{
    return{
        variables:{
          search:props.search,
          howmany: 30
        }
    }
  }
})(BookList);
